namespace Tangle.Net.Tests.Cryptography
{
  using Microsoft.VisualStudio.TestTools.UnitTesting;

  using Tangle.Net.Source.Cryptography;

  /// <summary>
  /// The private key test.
  /// </summary>
  [TestClass]
  public class PrivateKeyTest
  {
    #region Public Methods and Operators

    /// <summary>
    /// The test private key generation from one fragment.
    /// </summary>
    [TestMethod]
    public void TestPrivateKeyGenerationFromOneFragment()
    {
      var key =
        new PrivateKey(
          "");

      Assert.AreEqual("TIBCVWNGKLYX9UDSNQK9FWFODLGWOZREMZYWYRPILH9LYLOSRJSZQKKXBBEGVRJYTFHWQXRAMPMRJJT99", key.Digest);
    }

    #endregion
  }
}